import { Link} from "react-router-dom";
import React from "react";
import jwtDecode from "jwt-decode";

const UlogovanKorisnik = () => {
  const getUserRole = () => {
    var token = localStorage.getItem("token");
    if (token) {
      try {
        const decodedToken = jwtDecode(token);
        return decodedToken[
          "http://schemas.microsoft.com/ws/2008/06/identity/claims/role"
        ];
      } catch (error) {
        console.error("Error decoding token:", error);
      }
    }
    return null;
  };

  var role = getUserRole();
  console.log(role);

  return (
    <div>
      <h1 style={{ color: "#279980" }}>Pocetna stranica</h1>
  
      {(role === "Kupac" || role === "Prodavac" || role === "Admin") && (
        <>
          <Link to="/ulogovan-korisnik/profil" style={{ color: "#007bff" }}>
            Profil
          </Link>
          <p></p>
        </>
      )}
      {role === "Kupac" && (
        <>
          <Link
            to="/ulogovan-korisnik/pregled-artikala"
            style={{ color: "#007bff" }}
          >
            Nova porudzbina
          </Link>
          <p></p>
          <Link
            to="/ulogovan-korisnik/prethodne-porudzbine"
            style={{ color: "#007bff" }}
          >
            Prethodne porudzbine
          </Link>
          <p></p>
        </>
      )}
  
      {role === "Prodavac" && (
        <>
          <Link
            to="/ulogovan-korisnik/dodaj-artikal"
            style={{ color: "#28a745" }}
          >
            Dodaj artikal
          </Link>
          <p></p>
          <Link
            to="/ulogovan-korisnik/moje-porudzbine"
            style={{ color: "#28a745" }}
          >
            Moje porudzbine
          </Link>
          <p></p>
          <Link
            to="/ulogovan-korisnik/nove-porudzbine"
            style={{ color: "#28a745" }}
          >
            Nove porudzbine
          </Link>
          <p></p>
        </>
      )}
  
      {role === "Admin" && (
        <>
          <Link
            to="/ulogovan-korisnik/verifikacija"
            style={{ color: "#007bff" }}
          >
            Verifikacija
          </Link>
          <p></p>
          <Link
            to="/ulogovan-korisnik/sve-porudzbine"
            style={{ color: "#007bff" }}
          >
            Sve porudzbine
          </Link>
        </>
      )}
    </div>
  );
};

export default UlogovanKorisnik;
