        <div>
            <h1>Prethodne porudzbine</h1>
            <table>
                <thead>
                    <tr>
                        <th>Vreme isporuke</th>
                        <th>Adresa dostave</th>
                        <th>Artikli</th>
                        <th>Komentar</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td>{porudzbine.vremeIsporuke}</td>
                        <td>{porudzbine.adresaDostave}</td>
                        <td>{porudzbine.artikli}</td>
                        <td>{porudzbine.komentar}</td>
                    </tr>
                </tbody>
            </table>
        </div>
    );
};

