import React,{useRef, useState, useEffect} from "react";

const DodavanjeArtikla=()=>{

    const formRef=useRef(null);
    const [artikli, setArtikli] = useState([]);
    
    const azurirajArtikal=(artikal)=>{
        // Prvo, prikažite korisniku dijalog sa upitom za nove podatke artikla koje želi da ažurira
    const noviNaziv = prompt("Unesite novi naziv artikla:", artikal.naziv);
    const novaCena = parseFloat(prompt("Unesite novu cenu artikla:", artikal.cena));
    const novaKolicina = parseInt(prompt("Unesite novu količinu artikla:", artikal.kolicina));
    const noviOpis = prompt("Unesite novi opis artikla:", artikal.opis);
    const novaSlika = prompt("Unesite novu URL sliku artikla:", artikal.slika);

    // Provera da li je korisnik odustao od ažuriranja (klikom na Cancel)
    if (noviNaziv === null || novaCena === null || novaKolicina === null || noviOpis === null || novaSlika === null) {
        console.log("Ažuriranje artikla je otkazano.");
        return;
    }

    // Kreirajte objekat sa novim podacima artikla
    const noviArtikal = {
        ...artikal,
        prodavacId: artikal.prodavacId,
        naziv: noviNaziv,
        cena: novaCena,
        kolicina: novaKolicina,
        opis: noviOpis,
        slika: novaSlika,
    };

    // Pošaljite zahtev na server za ažuriranje artikla
    fetch(`https://localhost:44388/Artikal?idArtikla=${artikal.id}`, {
        method: "PUT", // Korišćenje PUT metode za ažuriranje podataka na serveru
        body: JSON.stringify(noviArtikal),
        headers: {
            "Content-Type": "application/json",
        },
        mode: "cors",
    })
        .then((response) => {
            if (!response.ok) {
                throw new Error("Network response was not ok");
            }
            // Ažurirajte listu artikala sa novim podacima
            setArtikli((prevArtikli) =>
                prevArtikli.map((a) => (a.id === artikal.id ? noviArtikal : a))
            );
            console.log("Artikal uspešno ažuriran.");
        })
        .catch((error) => {
            console.error("Greška prilikom ažuriranja artikla:", error);
        });

    };

    const [formData, setFormData]=useState({
        "prodavacId": 4, //zakucala na prodavac2
        "naziv": "string",
        "cena": 0,
        "kolicina": 0,
        "opis": "string",
        "slika": "string"
    });

    const obrisiArtikal=(artikal)=>{
        console.log("stigli smo dovde ale");
        const potvrdiBrisanje= window.confirm("Da li želite da obrišete ovaj artikal?");
        if(potvrdiBrisanje){
            fetch(`https://localhost:44388/Artikal?idArtikla=${artikal.id}`, {
                method: "DELETE",
                headers: {
                    "Content-Type": "application/json",
                },
                mode: "cors",
            })
            .then((response) => {
                if (!response.ok) {
                  throw new Error("Network response was not ok");
                }
                // Uklonite artikal iz liste nakon što ga uspešno obrišete
                setArtikli((prevArtikli) => prevArtikli.filter((a) => a.id !== artikal.id));
                console.log("Artikal uspešno obrisan.");
              })
              .catch((error) =>{
                console.error("Greška prilikom brisanja artikla:", error);
              });
        }
    };

    const handleChange=(e)=>{
        const {name, value} = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    useEffect(()=> {
        fetch("https://localhost:44388/Artikal", {
            method: "GET",
            headers: {
                "Content-Type":"application/json",
            },
            mode: "cors",
        })
            .then((response)=> response.json())
            .then((data)=> {
                setArtikli(data);
                console.log(data);
            })
            .catch((error)=>{
                console.error("Greška prilikom dohvatanja artikala:", error);
            });
    }, []);

    const handleSubmit=(e)=>{
        e.preventDefault();
        //dalje ide lgika za slanje
        const formDataToSend={
            prodavacId: formRef.current.prodavacId.value,
            naziv: formRef.current.naziv.value,
            cena:formRef.current.cena.value,
            kolicina:formRef.current.kolicina.value,
            opis:formRef.current.opis.value,
            slika:formRef.current.slika.value,
        };

        fetch("https://localhost:44388/Artikal", {
            method: "POST",
            body: JSON.stringify(formDataToSend),
            headers:{
                "Content-Type": "application/json",
            },
            mode: "cors",
        })
            .then((response)=> response.json())
            .then((data)=> {
                console.log("Podaci su uspesno poslati na server:", data);
            })
            .catch((error)=> {
                console.error("Greska prilikom slanja na server:", error);
            });
    };

    return(
        <div>
            <h1>Dodavanje artikla</h1>
            <form onSubmit={handleSubmit} ref={formRef}>
                <label hidden>
                    ProdavacId:
                    <input
                        type="number"
                        name="prodavacId"
                        value={formData.prodavacId}
                        onChange={handleChange}
                    />
                    <p></p>
                </label>
                <label>
                    Naziv:
                    <input
                        type="text"
                        name="naziv"
                        onChange={handleChange}
                    />
                    <p></p>
                </label>
                <label>
                    Cena:
                    <input
                        type="number"
                        name="cena"
                        onChange={handleChange}
                    />
                    <p></p>
                </label>
                <label>
                    Kolicina:
                    <input
                        type="number"
                        name="kolicina"
                        onChange={handleChange}
                    />
                    <p></p>
                </label>
                <label>
                    Opis:
                    <input
                        type="text"
                        name="opis"
                        onChange={handleChange}
                    />
                    <p></p>
                </label>
                <label>
                    Slika:
                    <input
                        type="text"
                        name="slika"
                        onChange={handleChange}
                    />
                    <p></p>
                </label>
                <button type="submit">Dodaj artikal</button>
            </form>

            <div>
                <h1>Prikaz svih artikala</h1>
                <ul>
                    <tr>
                        <th style={{color: "#279980"}}>Naziv</th>
                        <th style={{color: "#279980"}}>Cena</th>
                        <th style={{color: "#279980"}}>Kolicina</th>
                        <th style={{color: "#279980"}}>Opis</th>
                        <th style={{color: "#279980"}}>Slika</th>
                    </tr>
                    {artikli.map((artikal)=>(
                        <tr key={artikal.id}>
                            <td>{artikal.naziv}</td>
                            <td>{artikal.cena.toLocaleString("sr-RS", {style: "currency", currency: "RSD"})}</td>
                            <td>{artikal.kolicina}</td>
                            <td>{artikal.opis}</td>
                            <td>
                                <img src={artikal.slika} alt={artikal.naziv} style={{ width: "100px" }} />
                            </td>
                            <td>
                                <button type="button" onClick={()=>{
                                    azurirajArtikal(artikal)}}>Azuriraj</button>
                            </td>
                            <td>
                                <button type="button" onClick={()=> obrisiArtikal(artikal)}>Obrisi</button>
                            </td>

                        </tr>
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default DodavanjeArtikla;
