import React from "react";

const Korpa = ({ cartItems, obrisiIzKorpe }) => {
    if (cartItems.length === 0) {
      return <div style={{color: "#279980"}}><br/><br/>Korpa je trenutno prazna.</div>;
    }

    // Grupiranje artikala po prodavacId-u
    const groupedItems = cartItems.reduce((grouped, item) => {
        const { prodavacId } = item.artikal;
        if (!grouped[prodavacId]) {
            grouped[prodavacId] = { artikal: item.artikal, kolicina: 0 };
        }
        grouped[prodavacId].kolicina += item.kolicina;
        return grouped;
    }, {});
  
    return (
        <div>
          <h2>Artikli u korpi:</h2>
          <ul>
            {Object.values(groupedItems).map((groupedItem, index) => (
                <li key={index}>
                    {groupedItem.artikal.naziv} (Količina: {groupedItem.kolicina}){" "}
                    <button type="button" onClick={() => obrisiIzKorpe(groupedItem.artikal)}>
                Obriši
                    </button>
                </li>
                ))}
          </ul>
        </div>
      );
    };
   

export default Korpa;
