import React, { useState } from "react";

const NovaPorudzbina = ({ cartItems, ukupanIznos, handlePoruci }) => {
  const [adresa, setAdresa] = useState("");
  const [komentar, setKomentar] = useState("");

    const posaljiPorudzbinuNaServer=()=>{
        const porudzbina={
            "korisnikId": 4, //zakucala miru
            "adresaDostave": adresa,
            "komentar": komentar,
            "artikli": cartItems.map((item) => ({ id: item.artikal.id, kolicina: item.kolicina })),
            "vremeIsporuke": "2023-08-02T09:10:26.548Z"
        };

        fetch("https://localhost:44388/Porudzbina", {
            method:'POST',
            headers: {
                "Content-Type": "application/json",
              },
            body: JSON.stringify(porudzbina),
            mode: "cors",
        })
            .then((response)=> response.json())
            .then((data)=>{
                //odgovor servera
                alert("Porudžbina je uspešno poslata!");
            })
            .catch((error)=>{
                //greske prilikom slanja na server
                console.error("Greška prilikom slanja porudžbine:", error);
                alert("Došlo je do greške prilikom slanja porudžbine.");
            })
    }

  return (
    <div>
      <h2 style={{ color: "#279980" }}>Pregled odabranih artikala:</h2>
      <ul>
        {cartItems.map((item, index) => (
          <li key={index}>
            {item.artikal.naziv} (Količina: {item.kolicina})
          </li>
        ))}
      </ul>
      <div>
        <h2 style={{ color: "#279980" }}>Ukupan iznos:</h2>{" "}
        <h2>{ukupanIznos.toLocaleString("sr-RS", { style: "currency", currency: "RSD" })}</h2>
      </div>
      <div>
        <label htmlFor="adresa">Adresa:</label>
        <input
          type="text"
          id="adresa"
          value={adresa}
          onChange={(e) => setAdresa(e.target.value)}
        />
      </div>
      <div>
        <label htmlFor="komentar">Komentar:</label>
        <textarea
          id="komentar"
          value={komentar}
          onChange={(e) => setKomentar(e.target.value)}
        />
      </div>
      <button onClick={handlePoruci}>Poruči</button>
    </div>
  );
};

export default NovaPorudzbina;
