import React, { useState } from 'react';

const Registracija = () => {
  const [formData, setFormData] = useState({
    korisnickoIme: '',
    email: '',
    lozinka: '',
    ime: '',
    prezime: '',
    datumRodjenja: '',
    adresa: '',
    slika: null,
    tipKorisnika: 'Kupac',
    postarina: '0',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleImageChange = (e) => {
    setFormData({
      ...formData,
      slika: e.target.files[0],
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Ovde možete implementirati logiku za slanje podataka na server ili ih spremanje u lokalno skladište.
    fetch('https://localhost:44388/Korisnik', {
        method: 'POST',
        body: JSON.stringify(formData),
        headers: {
            'Content-Type': 'application/json',
        },
    })
    .then((Response)=> Response.json())
    .then((data)=>{
        //obrada odgovora servera
    })
    .catch((error) => {
        //obrada greske
    })
  };

  return (
    <div>
      <h1>Registracija</h1>
      <form onSubmit={handleSubmit}>
        <label htmlFor="korisnickoIme">Korisničko ime:</label>
        <input
          type="text"
          id="korisnickoIme"
          name="korisnickoIme"
          value={formData.korisnickoIme}
          onChange={handleChange}
          required
        /><br/>

        <label htmlFor="email">E-mail adresa:</label>
        <input
          type="email"
          id="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          required
        /><br/>

        <label htmlFor="lozinka">Lozinka:</label>
        <input
          type="password"
          id="lozinka"
          name="lozinka"
          value={formData.lozinka}
          onChange={handleChange}
          required
        /><br/>

        <label htmlFor="ime">Ime:</label>
        <input
          type="text"
          id="ime"
          name="ime"
          value={formData.ime}
          onChange={handleChange}
          required
        /><br/>

        <label htmlFor="prezime">Prezime:</label>
        <input
          type="text"
          id="prezime"
          name="prezime"
          value={formData.prezime}
          onChange={handleChange}
          required
        /><br/>

        <label htmlFor="datumRodjenja">Datum rođenja:</label>
        <input
          type="date"
          id="datumRodjenja"
          name="datumRodjenja"
          value={formData.datumRodjenja}
          onChange={handleChange}
          required
        /><br/>

        <label htmlFor="adresa">Adresa:</label>
        <input
          type="text"
          id="adresa"
          name="adresa"
          value={formData.adresa}
          onChange={handleChange}
          required
        /><br/>

        <label htmlFor="tipKorisnika">Tip korisnika:</label>
        <select
            id="tipKorisnika"
            name="tipKorisnika"
            value={formData.tipKorisnika}
            onChange={handleChange}
            required
        >
            <option value="kupac">Kupac</option>
            <option value="prodavac">Prodavac</option>
        </select>
        <br/>

        {formData.tipKorisnika === 'prodavac' && (
            <div>
            <label htmlFor="postarina">Postarina:</label>
            <input
              type="number"
              id="postarina"
              name="postarina"
              value={formData.postarina}
              onChange={handleChange}
              required
            />
            <br/>
          </div>
        )}

        <label htmlFor="slika">Slika profila:</label>
        <input
          type="file"
          id="slika"
          name="slika"
          onChange={handleImageChange}
          required
        /><br/>

        <button type="submit">Registruj se</button>
      </form>
    </div>
  );
};

export default Registracija;
