import React,{useRef, useState, useEffect} from "react";

const DodavanjeArtikla=()=>{

    const formRef=useRef(null);

    const [formData, setFormData]=useState({
        "prodavacId": 4, //zakucala na prodavac2
        "naziv": "string",
        "cena": 0,
        "kolicina": 0,
        "opis": "string",
        "slika": "string"
    });

    const handleChange=(e)=>{
        const {name, value} = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const handleSubmit=(e)=>{
        e.preventDefault();
        //dalje ide lgika za slanje
        const formDataToSend={
            naziv: formRef.current.naziv.value,
            cena:formRef.current.cena.value,
            kolicina:formRef.current.kolicina.value,
            opis:formRef.current.opis.value,
            slika:formRef.current.slika.value,
        };

        fetch("https://localhost:44388/Artikal", {
            method: "POST",
            body: JSON.stringify(formDataToSend),
            headers:{
                "Content-Type": "application/json",
            },
            mode: "cors",
        })
            .then((response)=> response.json())
            .then((data)=> {
                console.log("Podaci su uspesno poslati na server:", data);
            })
            .catch((error)=> {
                console.error("Greska prilikom slanja na server:", error);
            });
    };

    return(
        <div>
            <p>Dodavanje artikla</p>
            <form onSubmit={handleSubmit} ref={formRef}>
                <label>
                    Naziv:
                    <input
                        type="text"
                        name="naziv"
                        onChange={handleChange}
                    />
                    <p></p>
                </label>
                <label>
                    Cena:
                    <input
                        type="number"
                        name="cena"
                        onChange={handleChange}
                    />
                    <p></p>
                </label>
                <label>
                    Kolicina:
                    <input
                        type="number"
                        name="kolicina"
                        onChange={handleChange}
                    />
                    <p></p>
                </label>
                <label>
                    Opis:
                    <input
                        type="text"
                        name="opis"
                        onChange={handleChange}
                    />
                    <p></p>
                </label>
                <label>
                    Slika:
                    <input
                        type="text"
                        name="slika"
                        onChange={handleChange}
                    />
                    <p></p>
                </label>
                <button type="submit">Sacuvaj izmene</button>
            </form>
        </div>
    );
};

export default DodavanjeArtikla;
